#include<iostream>using namespace std;#define MAX 20int zagadka_req(int n,int k, int all[],int p, int in[]){	int s=0;	if(n==k||k==0)	{		for(int i=0;i<p;i++)			cout << in[i] <<" ";		for(int i=0;i<k;i++)			cout << all[i] <<" ";		cout << endl;		return 1;	}	in[p] = all[0];	s = zagadka_req (n-1,k-1,all+1,p+1,in);	s += zagadka_req (n-1,k,all+1,p,in);	return s;}int main(){	int tab[MAX];	int in[MAX];	int s;	for(int i = 0;i<MAX;i++) tab[i]= i+1;	s = zagadka_req(6,3,tab,0,in);	cout <<endl<<s<<endl;	return 0;}